window.addEventListener("elementor/frontend/init", function () {

  const widgetName = "codexshaper-framework-eb-widget-portfolio-slider";
  const skins = [
    "skin-portfolio-two",
    "skin-portfolio-three",
    "skin-portfolio-four",
    "skin-portfolio-five",
    "skin-portfolio-six",
  ];

  elementorFrontend.hooks.addFilter("cxf/widgets/sliders", function (sliders) {
    sliders[widgetName] = skins;

    return sliders;
  });
});
