window.addEventListener("elementor/frontend/init", function () {
  elementorFrontend.hooks.addAction(
    "frontend/element_ready/codexshaper-framework-eb-widget-service-grid.skin-service-grid-five",
    function (widget) {

      const items = document.querySelectorAll('.cdx-service-item-7');
      let activeThumb;
      let activeTitle;

      if (items.length > 0) {
        activeThumb = items[0].querySelector('.cdx-thum-wrap');
        activeThumb.classList.add('active');
        activeTitle = items[0].querySelector('.cdx-title');
        activeTitle.classList.add('active');
      }

      items.forEach(function (item) {
        const title = item.querySelector('.cdx-title');
        const thumbWrap = item.querySelector('.cdx-thum-wrap');

        title.addEventListener('mouseenter', function () {
          document.querySelectorAll('.cdx-thum-wrap').forEach(function (el) {
            el.classList.remove('active');
            document.querySelectorAll('.cdx-title').forEach(function (el) {
              el.classList.remove('active');
            });
          });
          thumbWrap.classList.add('active');
          activeThumb = thumbWrap;
          title.classList.add('active');
          activeTitle = title;
        });
      });

    }
  );
});
