window.addEventListener("elementor/frontend/init", function () {

  const widgetName = "codexshaper-framework-eb-widget-service-slider";
  const skins = [
    "skin-service-slider-two",
    "skin-service-slider-three",
    "skin-service-slider-four",
  ];

  elementorFrontend.hooks.addFilter("cxf/widgets/sliders", function (sliders) {
    sliders[widgetName] = skins;

    return sliders;
  });
});
