window.addEventListener("elementor/frontend/init", function () {
  const widgetName = "cxf--eb-widget-testimonial-slider";
  const skins = [
    "skin-testimonial-one",
    "skin-testimonial-two",
    "skin-testimonial-three",
    "skin-testimonial-four",
    "skin-testimonial-five",
    "skin-testimonial-six",
    "skin-testimonial-seven",
  ];

  elementorFrontend.hooks.addFilter("cxf/widgets/sliders", function (sliders) {
    sliders[widgetName] = skins;

    return sliders;
  });
});
