"use strict";
window.addEventListener("elementor/frontend/init", function () {
    elementorFrontend.hooks.addAction(
        "frontend/element_ready/cxf--offcanvas.default",
        function (widget) {

            var openButton = document.querySelector(".cdx-btn-open");
            var offcanvasWrapper = document.querySelector(".cxf-offcanvas-wrapper");
            var closeButton = document.querySelector(".offcanvas--close--button-js");


            openButton.addEventListener("click", function () {
                offcanvasWrapper.classList.add("show");
                openButton.classList.add("hide");
            }
            );

            closeButton.addEventListener("click", function () {
                {
                    offcanvasWrapper.classList.remove("show");
                    openButton.classList.remove("hide");
                }

            });
            var menuItems = document.querySelectorAll(".menu-item-has-children");
            menuItems.forEach(function (menuItem) {
                menuItem.addEventListener("click", function (event) {
                    event.stopPropagation();
                    menuItems.forEach(function (item) {
                        if (item !== menuItem) {
                            item.classList.remove("open");
                        }
                    });
                    menuItem.classList.toggle("open");
                });
            });

        });
});



